import React, {useEffect} from "react";
import {useUser} from "hook/User";
import styled from "styled-components";
import {useForm} from "react-hook-form";
import {useNavigate} from "react-router-dom";
import {cartDelete, cartUpdate, insertCart, retrieveCart} from "../backend/cart";
import TableContainer from "@mui/material/TableContainer";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import TableCell from "@mui/material/TableCell";
import TableBody from "@mui/material/TableBody";
import Button from '@mui/material/Button';


import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';



const StyledDiv = styled.div`
  display: flex;
  flex-direction: column;
`

const StyledH1 = styled.h1`
`

const StyledInput = styled.input`
`

const StyledButton = styled.button`
`

function createData(
    movieTitle,
    unitPrice,
    quantity,
    movieId
) {
    return { movieTitle, unitPrice, quantity, movieId };
}
let rows = [];
let added = 0;

const Cart = () => {
    const {accessToken} = useUser();

    // cart
    const [cart, setCart] = React.useState(0);
    const [total, setTotal] = React.useState(0);
    const navigate = useNavigate();

    const viewCart = () => {
        retrieveCart({}, accessToken)
            .then(response => {
                if (response.data.result.code == 3004) {
                    setCart([])
                }
                else {
                    setCart(response.data.items)

                }
                setTotal(response.data.total)
            })
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)));
    }

    const deleteMovie = (movieId, quantity) => {
        const payload = {
            movieId: movieId,
            quantity: quantity
        }

        cartDelete(payload, accessToken)
            .then(
                response => {
                    console.log(response.data.items)
                    setCart(response.data.items)
                    setTotal(response.data.total)
                    alert(JSON.stringify(response.data, null, 2))
                })
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)));
        window.location.reload();

    }


    for (let i = added; i < cart.length; i++) {
        rows.push(createData(cart[i].movieTitle, cart[i].unitPrice, cart[i].quantity, cart[i].movieId));
        added++;
    }

    console.log(cart)


    useEffect(() => viewCart(), []);

    return (
        <StyledDiv>
            <TableContainer component={Paper}>
                <Table sx={{ minWidth: 800 }} aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <TableCell><b>Movie Title</b></TableCell>
                            <TableCell align="right"><b>Price</b></TableCell>
                            <TableCell align="right"><b>Quantity</b></TableCell>
                            <TableCell align="right"><b> </b></TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {rows.map((row) => (
                            <TableRow
                                key={row.movieTitle}
                                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                            >
                                <TableCell component="th" scope="row">{row.movieTitle}</TableCell>
                                <TableCell align="right">{row.unitPrice}</TableCell>
                                <TableCell align="right">{row.quantity}</TableCell>
                                <TableCell align="right">
                                    <IconButton onClick={() => { deleteMovie(row.movieId, row.quantity)}}aria-label="delete">
                                        <DeleteIcon />
                                    </IconButton>
                                </TableCell>

                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            <br/>
            <br/>
            <TableContainer component={Paper}>
                <Table sx={{ maxWidth: 800 }} aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <TableCell><Button>Clear Cart</Button></TableCell>
                            <TableCell align="right"><b>TOTAL:</b> ${total}</TableCell>
                            <TableCell align="right">            </TableCell>
                            <TableCell align="right"><Button>Checkout</Button></TableCell>

                        </TableRow>
                    </TableHead>
                </Table>
            </TableContainer>

        </StyledDiv>

    );
}

export default Cart;
