import React, {useEffect} from "react";
import {useLocation, useNavigate, useParams} from 'react-router-dom';
import {useUser} from "../hook/User";
import styled from "styled-components";
import Movies from "../backend/movies";



const StyledDiv = styled.div`
  display: flex;
  align: center;
  flex-direction: column;
`

const StyledH1 = styled.h1`
    display: 'flex';
    justifyContent:'center';
     alignItems:'center;
`

const StyledP = styled.p`
    display: 'flex';
    justifyContent:'center';
    alignItems:'center;
`

const StyledButton = styled.button`
`
const MovieDetail = () => {
    const {
        accessToken, setAccessToken,
        refreshToken, setRefreshToken
    } = useUser();

    const [movie, setMovie] = React.useState(0);
    const [genres, setGenres] = React.useState(0);
    const [persons, setPersons] = React.useState(0);

    const {movieId} = useParams();
    const navigate = useNavigate();

    const getMovie = () => {
        // const title = null;
        // const year = null;
        // const director = null;
        // const rating = null;
        // const budget = null;
        // const genres = null;
        // const people = null;

        Movies.getMovieDetail({}, accessToken, movieId)
            .then(response => {
                console.log(response.data);
                setMovie(response.data.movie);
                setGenres(response.data.genres);
                setPersons(response.data.persons);
                console.log(movie);
                console.log(genres);
                console.log(persons);
            })
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)));
    }

    React.useEffect(() => getMovie(), []);

    const styles = {
        background: {
            backgroundImage: `url(${"https://image.tmdb.org/t/p/w185" + movie.backdropPath})`
        }
    }


    return (
        <div style={styles.background}>
            <StyledDiv>

                <h1>{movie.title}</h1>
                <p>{movie.overview}</p>
                <p>{movie.director}</p>
                <p>{movie.rating}</p>
                <p>{movie.budget}</p>
                <p>{movie.revenue}</p>
                {/*<p>{genres[0].name}</p>*/}
                {/*<p>{persons[0].name}</p>*/}
                <img src={"https://image.tmdb.org/t/p/w300" + movie.posterPath} alt="" id="img" />
                <img src={"https://image.tmdb.org/t/p/w185" + movie.backdropPath} alt="" id="img" />


            </StyledDiv>
        </div>

    );

}

export default MovieDetail;
