import React, {useEffect} from "react";
import {useUser} from "hook/User";
import styled from "styled-components";
import {useForm} from "react-hook-form";
import {useNavigate} from "react-router-dom";
import {cartDelete, cartUpdate, insertCart, retrieveCart} from "../backend/cart";
import TableContainer from "@mui/material/TableContainer";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import TableCell from "@mui/material/TableCell";
import TableBody from "@mui/material/TableBody";

import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';



const StyledDiv = styled.div`
  display: flex;
  flex-direction: column;
`

const StyledH1 = styled.h1`
`

const StyledInput = styled.input`
`

const StyledButton = styled.button`
`

function createData(
    movieTitle,
    unitPrice,
    quantity,
    movieId
) {
    return { movieTitle, unitPrice, quantity, movieId };
}
const rows = [];



const Cart = () => {
    const {accessToken} = useUser();

    // cart
    const [cart, setCart] = React.useState(0);
    const [total, setTotal] = React.useState(0);
    const navigate = useNavigate();

    const viewCart = () => {
        retrieveCart({}, accessToken)
            .then(response => {
                setCart(response.data.items)
                setTotal(response.data.total)
                console.log(response.data.items)
            })
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)));
    }

    const deleteMovie = (movieId, quantity) => {
        const payload = {
            movieId: movieId,
            quantity: quantity
        }

        cartDelete(payload, accessToken)
            .then(
                response => {
                    alert(JSON.stringify(response.data, null, 2))
                })
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)));

        let data = response.data.items;
        console.log(this.state.data[row.index]);
        data.splice(row.index, 1)
        this.setState({data})
    }

    const backHome = () => {
        navigate("/");
        window.location.reload();
    }

    const updateCart = (event, movieId) => {
        const payload = event.target.value > 0 ? {
            movieId: movieId,
            quantity: event.target.value
        } : {
            movieId: movieId
        }

        event.target.value > 0
            ? cartUpdate(payload, accessToken)
                .catch(error => alert(JSON.stringify(error.response.data, null, 2)))
            : cartDelete(payload, accessToken)
                .catch(error => alert(JSON.stringify(error.response.data, null, 2)));

        window.location.reload();
    }

    for (let i = 0; i < cart.length; i++) {
        rows.push(createData(cart[i].movieTitle, cart[i].unitPrice, cart[i].quantity, cart[i].movieId));
    }
    console.log(rows);

    useEffect(() => viewCart(), []);

    return (
        <StyledDiv>
        <TableContainer component={Paper}>
            <Table sx={{ minWidth: 800 }} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell><b>Movie Title</b></TableCell>
                        <TableCell align="right"><b>Price</b></TableCell>
                        <TableCell align="right"><b>Quantity</b></TableCell>
                        <TableCell align="right"><b> </b></TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {rows.map((row) => (
                        <TableRow
                            key={row.movieTitle}
                            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                        >
                            <TableCell component="th" scope="row">
                                {row.movieTitle}
                            </TableCell>
                            <TableCell align="right">{row.unitPrice}</TableCell>
                            <TableCell align="right">{row.quantity}</TableCell>
                            <TableCell align="right">
                                <IconButton onClick={() => { deleteMovie(row.movieId, row.quantity)}}aria-label="delete">
                                    <DeleteIcon />
                                </IconButton>
                            </TableCell>

                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
        </StyledDiv>

    );
}

export default Cart;
