import React, {useEffect} from "react";
import {useUser} from "hook/User";
import styled from "styled-components";
import {useForm} from "react-hook-form";
import {useNavigate} from "react-router-dom";
import {cartDelete, cartUpdate, retrieveCart} from "../backend/cart";
import TableContainer from "@mui/material/TableContainer";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import TableCell from "@mui/material/TableCell";
import TableBody from "@mui/material/TableBody";


const StyledDiv = styled.div`
  display: flex;
  flex-direction: column;
`

const StyledH1 = styled.h1`
`

const StyledInput = styled.input`
`

const StyledButton = styled.button`
`

function createData(
    movieTitle: string,
    unitPrice: number,
    quantity: number,

) {
    return { movieTitle, unitPrice, quantity };
}
const rows = [];




const Cart = () => {
    const {accessToken} = useUser();

    // cart
    const [cart, setCart] = React.useState(0);
    const [total, setTotal] = React.useState(0);
    const navigate = useNavigate();

    const viewCart = () => {
        retrieveCart({}, accessToken)
            .then(response => {
                setCart(response.data.items)
                setTotal(response.data.total)
                console.log(response.data.items)
            })
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)));
    }

    const backHome = () => {
        navigate("/");
        window.location.reload();
    }

    const updateCart = (event, movieId) => {
        const payload = event.target.value > 0 ? {
            movieId: movieId,
            quantity: event.target.value
        } : {
            movieId: movieId
        }

        event.target.value > 0
            ? cartUpdate(payload, accessToken)
                .catch(error => alert(JSON.stringify(error.response.data, null, 2)))
            : cartDelete(payload, accessToken)
                .catch(error => alert(JSON.stringify(error.response.data, null, 2)));

        window.location.reload();
    }

    for (let i = 0; i < cart.length; i++) {
        rows.push(createData(cart[i].movieTitle, cart[i].unitPrice, cart[i].quantity));
    }

    useEffect(() => viewCart(), []);

    return (
        // <StyledDiv>
        //     <TableContainer component={Paper}>
        //         <Table sx={{ minWidth: 1000 }} aria-label="simple table">
        //             <TableHead>
        //                 <TableRow>
        //                     <TableCell>Movie Title</TableCell>
        //                     <TableCell>Price</TableCell>
        //                     <TableCell>Quantity</TableCell>
        //                 </TableRow>
        //             </TableHead>
        //             <TableBody>
        //                 {rows.map((row) => (
        //                     <TableRow
        //                         key={row.name}
        //                         sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
        //                     >
        //                         <TableCell component="th" scope="row">
        //                             {row.name}
        //                         </TableCell>
        //                         <TableCell>{row.movieTitle}</TableCell>
        //                         <TableCell>{row.unitPrice}</TableCell>
        //                         <TableCell>{row.quantity}</TableCell>
        //                     </TableRow>
        //                 ))}
        //             </TableBody>
        //         </Table>
        //     </TableContainer>
        <StyledDiv>
        <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell>Dessert (100g serving)</TableCell>
                        <TableCell align="right">Calories</TableCell>
                        <TableCell align="right">Fat&nbsp;(g)</TableCell>
                        <TableCell align="right">Carbs&nbsp;(g)</TableCell>
                        <TableCell align="right">Protein&nbsp;(g)</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {rows.map((row) => (
                        <TableRow
                            key={row.name}
                            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                        >
                            <TableCell component="th" scope="row">
                                {row.name}
                            </TableCell>
                            <TableCell align="right">{row.calories}</TableCell>
                            <TableCell align="right">{row.fat}</TableCell>
                            <TableCell align="right">{row.carbs}</TableCell>
                            <TableCell align="right">{row.protein}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
        </StyledDiv>

    );
}

export default Cart;
