import React, {useEffect} from "react";
import {useLocation, useNavigate, useParams} from 'react-router-dom';
import {useUser} from "../hook/User";
import styled from "styled-components";
import Movies from "../backend/movies";
import BasicTable from "./BasicTable";
import TableContainer from "@mui/material/TableContainer";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import TableCell from "@mui/material/TableCell";
import TableBody from "@mui/material/TableBody";

{/*<h1>{movie.title}</h1>*/}
{/*<p>Overview {movie.overview}</p>*/}
{/*<p>{movie.director}</p>*/}
{/*<p>{movie.rating}</p>*/}
{/*<p>{movie.budget}</p>*/}
{/*<p>{movie.revenue}</p>*/}
{/*/!*<p>{genres[0].name}</p>*!/*/}
{/*/!*<p>{persons[0].name}</p>*!/*/}
{/*<img src={"https://image.tmdb.org/t/p/w200" + movie.posterPath}/>*/}

const Title = styled.div`
  display: flex;
  flex-direction: center;
`

const Column = styled.div`
  display: flex;
  flex-direction: column;
  max-width: 500px;
  gap: 8px;`

const Row = styled.div`
  display: flex;
  flex-direction: row;
  gap: 25px;

`

const StyledH1 = styled.h1`
    display: 'flex';
    justifyContent:'center';
     alignItems:'center;
`

const StyledP = styled.p`
    display: 'flex';
    justifyContent:'center';
    alignItems:'center;
`

const StyledButton = styled.button`
`
const MovieDetail = () => {
    const {
        accessToken, setAccessToken,
        refreshToken, setRefreshToken
    } = useUser();

    const [movie, setMovie] = React.useState(0);
    const [genres, setGenres] = React.useState(0);
    const [persons, setPersons] = React.useState(0);

    const {movieId} = useParams();
    const navigate = useNavigate();

    const getMovie = () => {
        // const title = null;
        // const year = null;
        // const director = null;
        // const rating = null;
        // const budget = null;
        // const genres = null;
        // const people = null;

        Movies.getMovieDetail({}, accessToken, movieId)
            .then(response => {
                console.log(response.data);
                setMovie(response.data.movie);
                setGenres(response.data.genres);
                setPersons(response.data.persons);
                console.log(movie);
                console.log(genres);
                console.log(persons);
            })
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)));
    }

    React.useEffect(() => getMovie(), []);


    function createData(
        name: string,
        calories: number,
        fat: number,
        carbs: number,
        protein: number,
    ) {
        return { name, calories, fat, carbs, protein };
    }

    const rows = [

    ];
    return (
        <Row>
            <Column>
                <img src={"https://image.tmdb.org/t/p/w200" + movie.posterPath}/>
            </Column>
            <Column>
            <TableContainer component={Paper}>
                <Table sx={{ maxWidth: 600 }} aria-label="simple table">
                    <TableHead>
                        {/*<TableRow>*/}
                        {/*    <TableCell>{movie.title}</TableCell>*/}
                        {/*    <TableCell><img src={"https://image.tmdb.org/t/p/w200" + movie.posterPath}/></TableCell>*/}
                        {/*</TableRow>*/}
                        <TableRow>
                            <TableCell>Overview</TableCell>
                            <TableCell>{movie.overview}</TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell>Director</TableCell>
                            <TableCell>{movie.director}</TableCell>

                        </TableRow>
                        <TableRow>
                            <TableCell>Rating</TableCell>
                            <TableCell>{movie.rating}/10</TableCell>

                        </TableRow>
                        <TableRow>
                            <TableCell>Budget</TableCell>
                            <TableCell>{movie.budget}</TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell>Revenue</TableCell>
                            <TableCell>{movie.revenue}</TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell>Genres</TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell>People</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {rows.map((row) => (
                            <TableRow
                                key={row.name}
                                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                            >
                                <TableCell component="th" scope="row">
                                    {row.name}
                                </TableCell>
                                <TableCell align="right">{row.calories}</TableCell>
                                <TableCell align="right">{row.fat}</TableCell>
                                <TableCell align="right">{row.carbs}</TableCell>
                                <TableCell align="right">{row.protein}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            </Column>

        </Row>


    );

}

export default MovieDetail;
